def read_google_sheet(sheet_name="Release Demo", secret_key_file='secret.json'):
    scope = ['https://spreadsheets.google.com/feeds','https://www.googleapis.com/auth/drive']
    creds = ServiceAccountCredentials.from_json_keyfile_name(secret_key_file, scope)
    client = gspread.authorize(creds)
    sheet = client.open(sheet_name).sheet1
    list_of_records = sheet.get_all_records()

    # list_of_records = MOCK_DATA

    return list_of_records